func gf256Inv(a byte) byte {
	// 0 没有逆；非 0：a^(−1) = exp(255 - log(a))
	if a == 0 {
		return 0
	}
	la := int(gf256log[a]) // 0..254
	return gf256exp[(0xFF-la)%0xFF]
}

func Scramble(buf []byte) {
	if len(buf) != 16 {
		panic(fmt.Sprintf("Scramble expects len=16, got %d", len(buf)))
	}

	var invSBox [1024]byte
	for i := 0; i < 4; i++ {
		base := i << 8
		for j := 0; j < 256; j++ {
			invSBox[base+int(blb3SBox[base+j])] = byte(j)
		}
	}

	var invMul [8]byte
	for i := 0; i < 8; i++ {
		invMul[i] = gf256Inv(blb3Mul[i])
	}
	var vec [16]byte
	for r := 0; r < 3; r++ {
		base := r << 4
		for j := 0; j < 16; j++ {
			idx := j & 7
			rowBase := (j & 3) << 8
			k := blb3siftow[base+j]
			vec[k] = gf256Mul(invMul[idx], invSBox[rowBase+int(buf[j]^blb3Key[idx])])
		}
		copy(buf, vec[:])
	}
}

// Descramble:
// for i in 0..2:
//
//	for j in 0..len-1:
//	  k = BlbShiftRow[(2-i)*0x10 + j]
//	  idx=j%8
//	  vector[j] = BlbKey[idx] ^ BlbSBox[(j%4*0x100) | GF256Mul(BlbMul[idx], buf[k%len])]
//	copy vector->buf
func Descramble(buf []byte) error {
	n := len(buf)
	if n == 0 {
		return nil
	}
	need := 0x20 + n // 32 + n
	if len(blb3siftow) < need {
		return fmt.Errorf("blb3siftow too short: have=%d need>=%d (bufLen=%d)", len(blb3siftow), need, n)
	}

	vector := make([]byte, n)
	for i := 0; i < 3; i++ {
		base := (2 - i) * 0x10
		for j := 0; j < n; j++ {
			k := int(blb3siftow[base+j])
			idx := j % 8
			m := gf256Mul(blb3Mul[idx], buf[k%n])
			sboxIndex := (j%4)*0x100 | int(m)
			vector[j] = blb3Key[idx] ^ blb3SBox[sboxIndex]
		}
		copy(buf, vector)
	}
	return nil
}
